    this.money = parseFloat(this.$route.query.money).toFixed(2);
    this.orderNo = this.$route.query.orderNo;
  },
  methods: {
    pay: function pay() {
      var _this = this;

      this.request.get("/api/order/paid/" + this.orderNo).then(function (res) {
        if (res.code === '200') {
          alert("您成功支付" + _this.money + "元");

          _this.$router.replace("/orderlist");
        } else {
          _this.$message.error(res.msg);
        }
      });
    }
